import React,{useState} from 'react';
import { TouchableOpacity, StyleSheet } from 'react-native';
import { NativeBaseProvider, Text, Box , extendTheme, Button} from 'native-base';
import InputButton from './src/components/inputButton';


const buttons =  [
  ['CLEAR', 'DEL'],
  ["7", "8" ,"9", '/'],
  ["4", "5", "6", "x"],
  ["1", "2", "3", "-"],
  ["0", ".", "=", "+"]
];

export default function App() {
  const theme = extendTheme({
      // Add new color
      primary: {
        50: '#E3F2F9',
        100: '#C5E4F3',
        200: '#A2D4EC',
        300: '#7AC1E4',
        400: '#47A9DA',
        500: '#0088CC',
        600: '#007AB8',
        700: '#006BA1',
        800: '#005885',
        900: '#003F5E',
      },
      // Redefinig only one shade, rest of the color will remain same.
      amber: {
        400: '#d97706',
      },
  });

  return (
    <NativeBaseProvider theme={theme}>

        {buttons.map((buttonRows,idx)=>(
          <Box style={style.container}>
            {buttonRows.map((buttonitem, idx)=>(
              <Button bg='primary.900' style={style.button} onPress={()=> handleInput(buttonitem)} key={idx}>
                <Text style={style.text}>{buttonitem}</Text>
              </Button>
            ))}
            <br/>
          </Box>
        )
        )}

    </NativeBaseProvider>
  );
}

const style = StyleSheet.create({
  container : {
    flexDirection: "row"
  },
  button : {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center'
    // width: '40px',
    // height: '40px',
    // borderRadius : "50%"
  },
  text:{
    color: '#fff'
  }
})
