import React, { useState } from "react";
import { TouchableOpacity, StyleSheet } from "react-native";
import {
  NativeBaseProvider,
  Text,
  Box,
  extendTheme,
  Button,
  Pressable,
} from "native-base";
import InputButton from "./src/components/inputButton";

const buttons = [
  ["CLEAR", "DEL"],
  ["7", "8", "9", "/"],
  ["4", "5", "6", "x"],
  ["1", "2", "3", "-"],
  ["0", ".", "=", "+"],
];

export default function App() {
  const theme = extendTheme({
    // Add new color
    primary: {
      50: "#E3F2F9",
      100: "#C5E4F3",
      200: "#A2D4EC",
      300: "#7AC1E4",
      400: "#47A9DA",
      500: "#0088CC",
      600: "#007AB8",
      700: "#006BA1",
      800: "#005885",
      900: "#003F5E",
    },
    // Redefinig only one shade, rest of the color will remain same.
    amber: {
      400: "#d97706",
    },
  });

  return (
    <NativeBaseProvider theme={theme}>
      <Box bg="primary.700" style={style.container}>
        <Box style={style.input}>
          <Text fontSize={30} style={style.result}>
            0
          </Text>
        </Box>
        <Box style={style.buttons}>
          {buttons.map((buttonRows, idx) => (
            <Box style={style.inputRow}>
              {buttonRows.map((buttonitem, idx) => (
                <Pressable style={style.button}>
                  {({ isHovered, isFocused, isPressed }) => {
                    return (
                      <Box
                        bg={isPressed ? "primary.900" : "primary.400"}
                        p={4}
                        rounded={10}
                        style={{
                          transform: [
                            {
                              scale: isPressed ? 0.93 : 1,
                            },
                          ],
                        }}
                      >
                        <Text color="#fff">{buttonitem}</Text>
                      </Box>
                    );
                  }}
                </Pressable>
              ))}
              <br />
            </Box>
          ))}
        </Box>
      </Box>
    </NativeBaseProvider>
  );
}

const style = StyleSheet.create({
  container: {
    minHeight: "100vh",
  },
  input: {
    // height: "35vh",
    flex: 4.5,
  },
  result: {},
  buttons: {
    // height: "65vh",
    flex: 5.5,
  },
  inputRow: {
    flexDirection: "row",
  },
  button: {
    flex: 1,
    display: "block",
    alignItems: "center",
    textAlign: "center",
    margin: 1.5,
    marginBottom: 8,
  },
  text: {
    color: "#fff",
  },
});
