import React, { useState, useEffect } from "react";
import { TouchableOpacity, StyleSheet, View } from "react-native";
import {
  NativeBaseProvider,
  Text,
  Box,
  extendTheme,
  Button,
  Pressable,
} from "native-base";
import { NavigationContainer, StackActions } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import InputButton from "./src/components/inputButton";
import Calculator from "./src/screen/calculator";

function SplashScreen({ navigation }) {
  useEffect(() => {
    setTimeout(() => {
      navigation.dispatch(StackActions.replace("Calculator"));
    }, 500);
  }, []);

  return (
    <View>
      <Text>This is splashScreen</Text>
    </View>
  );
}

const Stack = createNativeStackNavigator();
export default function App() {
  const theme = extendTheme({
    // Add new color
    primary: {
      50: "#E3F2F9",
      100: "#C5E4F3",
      200: "#A2D4EC",
      300: "#7AC1E4",
      400: "#47A9DA",
      500: "#0088CC",
      600: "#007AB8",
      700: "#006BA1",
      800: "#005885",
      900: "#003F5E",
    },
    // Redefinig only one shade, rest of the color will remain same.
    amber: {
      400: "#d97706",
    },
  });
  return (
    <NativeBaseProvider theme={theme}>
      <NavigationContainer>
        <Stack.Navigator>
          <Stack.Screen
            name="SplashCreen"
            component={SplashScreen}
            options={{
              headerShown: false,
            }}
          />
          <Stack.Screen
            name="Calculator"
            component={Calculator}
            options={{
              headerShown: false,
            }}
          />
        </Stack.Navigator>
      </NavigationContainer>
    </NativeBaseProvider>
  );
}

const style = StyleSheet.create({
  container: {
    minHeight: "100vh",
    padding: 6,
  },
  resultContainer: {
    // height: "35vh",
    flex: 4.5,
    justifyContent: "flex-end",
    alignItems: "flex-end",
    marginBottom: 10,
    flexDirection: "row",
  },
  result: {
    flex: 1,
    marginHorizontal: 5,
    paddingHorizontal: 5,
    textAlign: "end",
  },
  inputContainer: {
    // height: "65vh",
    flex: 5.5,
  },
  inputRow: {
    flexDirection: "row",
  },
  button: {
    flex: 1,
    display: "block",
    alignItems: "center",
    textAlign: "center",
    margin: 1.5,
    marginBottom: 8,
  },
  text: {
    color: "#fff",
  },
});
