import React, { useState } from "react";
import { TouchableOpacity, StyleSheet } from "react-native";
import {
  NativeBaseProvider,
  Text,
  Box,
  extendTheme,
  Button,
  Pressable,
} from "native-base";
import InputButton from "./src/components/inputButton";

const buttons = [
  ["CLEAR", "DEL"],
  ["7", "8", "9", "/"],
  ["4", "5", "6", "x"],
  ["1", "2", "3", "-"],
  ["0", ".", "=", "+"],
];

export default function App() {
  const [displayValue, setDisplayValue] = useState("0");
  const [nextValue, setNextValue] = useState(false);
  const [operator, setOperator] = useState("");
  const [firstValue, setFirstValue] = useState("");
  const [secondValue, setSecondValue] = useState("");
  const [value, setValue] = useState(false);

  const theme = extendTheme({
    // Add new color
    primary: {
      50: "#E3F2F9",
      100: "#C5E4F3",
      200: "#A2D4EC",
      300: "#7AC1E4",
      400: "#47A9DA",
      500: "#0088CC",
      600: "#007AB8",
      700: "#006BA1",
      800: "#005885",
      900: "#003F5E",
    },
    // Redefinig only one shade, rest of the color will remain same.
    amber: {
      400: "#d97706",
    },
  });

  const handleInput = (input) => {
    switch (input) {
      case "1":
      case "2":
      case "3":
      case "4":
      case "5":
      case "6":
      case "7":
      case "8":
      case "9":
      case "0":
        if (!value) {
          console.log("value false");
          if (secondValue == "" || ".") {
            setDisplayValue(displayValue != "0" ? displayValue + input : input);
            console.log("second value empty");
            if (!nextValue) {
              setFirstValue(displayValue != "0" ? displayValue + input : input);
              console.log("nextValue false");
            } else {
              if (secondValue == "0") {
                setSecondValue(input);
                setDisplayValue(displayValue.slice(0, -1));
              } else {
                setSecondValue(
                  secondValue == "." ? "0" + input : secondValue + input
                );
              }
              console.log("nextValue true");
            }
          } else if (secondValue != "" && secondValue != ".") {
            console.log("secondValue is'nt emty");
            setDisplayValue(input);
            setFirstValue(input);
            setOperator(operator);
            setSecondValue("");
          }
        } else {
          if (secondValue == "") {
            setDisplayValue(input);
            console.log("second value empty 2");
            if (!nextValue) {
              setFirstValue(input);
              console.log("nextValue false");
            } else {
              setSecondValue(secondValue + input);
              console.log("nextValue true");
            }
          } else {
            console.log("secondValue is'nt emty");
            setDisplayValue(input);
            setFirstValue(input);
            setOperator(operator);
            setSecondValue("");
          }
          setValue(false);
        }
        break;
      case "+":
      case "-":
      case "x":
      case "/":
        if (firstValue == "") {
          setFirstValue("0");
        }
        if (secondValue == "") {
          setOperator(input == "x" ? "*" : input);
          setDisplayValue(
            nextValue == false
              ? displayValue + input
              : displayValue.substr(0, displayValue.length - 1) + input
          );
        } else {
          let result = eval(firstValue + operator + secondValue);
          setDisplayValue(
            (result % 1 === 0 ? result : result.toFixed(3)) + input
          );
          setFirstValue(result % 1 === 0 ? result : result.toFixed(3));
          setOperator(input == "x" ? "*" : input);
          setSecondValue("");
          setValue(true);
        }
        setNextValue(true);
        break;
      case "=":
        if (firstValue != "" && operator != "" && secondValue == "") {
          setDisplayValue(firstValue);
          setNextValue(false);
          setOperator("");
          console.log("maybe wrong this");
          break;
        }
        let result = eval(firstValue + operator + secondValue);
        if (result % 1 != 0) {
          var arrResult = result.toString().split(".");
        }
        setFirstValue(
          result % 1 === 0
            ? result
            : arrResult[1].length < 3
            ? result
            : result.toFixed(3)
        );
        setDisplayValue(
          !nextValue
            ? displayValue
            : secondValue == ""
            ? firstValue
            : result % 1 === 0
            ? result
            : arrResult[1].length < 3
            ? result
            : result.toFixed(3)
        );
        setSecondValue("");
        setOperator("");
        setValue(true);
        setNextValue(false);
        break;
      case "CLEAR":
        setDisplayValue("0");
        setNextValue(false);
        setValue(false);
        setOperator("");
        setFirstValue("");
        setSecondValue("");
        break;
      case ".":
        console.log("method search " + firstValue.indexOf("."));

        if (firstValue.indexOf(".") == -1 && nextValue == false) {
          console.log("tes");
          setDisplayValue(displayValue + input);
          console.log("second value empty");
          if (!nextValue) {
            setFirstValue(firstValue + input);
            console.log("nextValue false");
          } else {
            setSecondValue(
              secondValue == "" ? "0" + input : secondValue + input
            );
            console.log("nextValue true");
          }
        } else if (
          secondValue.indexOf(".") == -1 &&
          nextValue == true &&
          operator != ""
        ) {
          if (secondValue == "") {
            setSecondValue("0" + input);
            setDisplayValue(displayValue + "0" + input);
          } else {
            setDisplayValue(displayValue + input);
            setSecondValue(secondValue + input);
          }
        }
        break;
    }
  };

  return (
    <NativeBaseProvider theme={theme}>
      <Box bg="primary.700" style={style.container}>
        <Box style={style.resultContainer}>
          <Box bg="primary.800" style={style.result}>
            {"nextValue : " + nextValue}
            {"value : " + value}
            {"first : " + firstValue}
            {"operator : " + operator}
            {"second : " + secondValue}
            <Text fontSize={50} color="#fff">
              {displayValue}
            </Text>
          </Box>
        </Box>
        <Box style={style.inputContainer}>
          {buttons.map((buttonRows, idx) => (
            <Box style={style.inputRow}>
              {buttonRows.map((buttonitem, idx) => (
                <Pressable
                  onPress={() => handleInput(buttonitem)}
                  style={style.button}
                >
                  {({ isHovered, isFocused, isPressed }) => {
                    return (
                      <Box
                        bg={isPressed ? "primary.900" : "primary.400"}
                        p={4}
                        rounded={10}
                        style={{
                          transform: [
                            {
                              scale: isPressed ? 0.93 : 1,
                            },
                          ],
                        }}
                      >
                        <Text color="#fff">{buttonitem}</Text>
                      </Box>
                    );
                  }}
                </Pressable>
              ))}
              <br />
            </Box>
          ))}
        </Box>
      </Box>
    </NativeBaseProvider>
  );
}

const style = StyleSheet.create({
  container: {
    minHeight: "100vh",
    padding: 6,
  },
  resultContainer: {
    // height: "35vh",
    flex: 4.5,
    justifyContent: "flex-end",
    alignItems: "flex-end",
    marginBottom: 10,
    flexDirection: "row",
  },
  result: {
    flex: 1,
    marginHorizontal: 5,
    paddingHorizontal: 5,
    textAlign: "end",
  },
  inputContainer: {
    // height: "65vh",
    flex: 5.5,
  },
  inputRow: {
    flexDirection: "row",
  },
  button: {
    flex: 1,
    display: "block",
    alignItems: "center",
    textAlign: "center",
    margin: 1.5,
    marginBottom: 8,
  },
  text: {
    color: "#fff",
  },
});
