import React, { useState } from "react";
import { TouchableOpacity, StyleSheet } from "react-native";
import {
  NativeBaseProvider,
  Text,
  Box,
  extendTheme,
  Button,
  Pressable,
} from "native-base";
import InputButton from "./src/components/inputButton";

const buttons = [
  ["CLEAR", "DEL"],
  ["7", "8", "9", "/"],
  ["4", "5", "6", "x"],
  ["1", "2", "3", "-"],
  ["0", ".", "=", "+"],
];

export default function App() {
  const [result, setResult] = useState("0");
  const [nextValue, setNextValue] = useState("0");
  const [operator, setOperator] = useState("");
  const [firstValue, setFirstValue] = useState("");
  const [secondValue, setSecondValue] = useState("");

  const theme = extendTheme({
    // Add new color
    primary: {
      50: "#E3F2F9",
      100: "#C5E4F3",
      200: "#A2D4EC",
      300: "#7AC1E4",
      400: "#47A9DA",
      500: "#0088CC",
      600: "#007AB8",
      700: "#006BA1",
      800: "#005885",
      900: "#003F5E",
    },
    // Redefinig only one shade, rest of the color will remain same.
    amber: {
      400: "#d97706",
    },
  });

  const handleInput = (input) => {
    switch (input) {
      case "1":
      case "2":
      case "3":
      case "4":
      case "5":
      case "6":
      case "7":
      case "8":
      case "9":
      case "0":
        setResult(result != "0" ? result + input : input);
        if (!nextValue) {
          setFirstValue(firstValue + input);
        } else {
          setSecondValue(secondValue + input);
        }
        break;
      case "+":
      case "-":
      case "x":
      case "/":
        input = input == "x" ? "*" : input;
        setResult(
          nextValue == false
            ? result + input
            : result.substr(0, result.length - 1) + input
        );
        setNextValue(true);
        break;
      case "=":
        setResult(eval(result));
        setNextValue(false);
    }
  };

  return (
    <NativeBaseProvider theme={theme}>
      <Box bg="primary.700" style={style.container}>
        <Box style={style.resultContainer}>
          <Box bg="primary.800" style={style.result}>
            first : {firstValue}
            second : {secondValue}
            <Text fontSize={50} color="#fff">
              {result}
            </Text>
          </Box>
        </Box>
        <Box style={style.inputContainer}>
          {buttons.map((buttonRows, idx) => (
            <Box style={style.inputRow}>
              {buttonRows.map((buttonitem, idx) => (
                <Pressable
                  onPress={() => handleInput(buttonitem)}
                  style={style.button}
                >
                  {({ isHovered, isFocused, isPressed }) => {
                    return (
                      <Box
                        bg={isPressed ? "primary.900" : "primary.400"}
                        p={4}
                        rounded={10}
                        style={{
                          transform: [
                            {
                              scale: isPressed ? 0.93 : 1,
                            },
                          ],
                        }}
                      >
                        <Text color="#fff">{buttonitem}</Text>
                      </Box>
                    );
                  }}
                </Pressable>
              ))}
              <br />
            </Box>
          ))}
        </Box>
      </Box>
    </NativeBaseProvider>
  );
}

const style = StyleSheet.create({
  container: {
    minHeight: "100vh",
    padding: 6,
  },
  resultContainer: {
    // height: "35vh",
    flex: 4.5,
    justifyContent: "flex-end",
    alignItems: "flex-end",
    marginBottom: 10,
    flexDirection: "row",
  },
  result: {
    flex: 1,
    marginHorizontal: 5,
    paddingHorizontal: 5,
    textAlign: "end",
  },
  inputContainer: {
    // height: "65vh",
    flex: 5.5,
  },
  inputRow: {
    flexDirection: "row",
  },
  button: {
    flex: 1,
    display: "block",
    alignItems: "center",
    textAlign: "center",
    margin: 1.5,
    marginBottom: 8,
  },
  text: {
    color: "#fff",
  },
});
